function ChatManager(a){this.chatWindow=a;this.activeVisitor=undefined;this.noChatSession=document.createElement("DIV");this.noChatSession.id="no-chat-session-selected";this.noChatSession.innerHTML="No visitor selected";a.appendChild(this.noChatSession)}ChatManager.prototype.showVisitorChat=function(a){this.activeVisitor=a;this.chatWindow.innerHTML="";this.chatWindow.appendChild(a.chatSession.getContainer())};ChatManager.prototype.clear=function(){this.chatWindow.innerHTML="";this.chatWindow.appendChild(this.noChatSession)};